import React, { Component } from "react";
import { PreviewPersonalData } from './components/Preview'
import './App.css'

class App extends Component {
  constructor() {
    super()
    this.state = {
      personalData: {
        firstName: "Pablo",
        lastName: "Escobar",
        birthDate: "1949-12-01",
        email: "pablo@gmail.com",
        phoneNumber: "601101997"
      },
      education: {},
      experience: {}

    }
    this.handleChangeFirstName = this.handleChangeFirstName.bind(this)
    this.handleChangeLastName = this.handleChangeLastName.bind(this)
    this.handleChangeBirthDate = this.handleChangeBirthDate.bind(this)
    this.handleChangeEmail = this.handleChangeEmail.bind(this)
    this.handleChangeNumber = this.handleChangeNumber.bind(this)
  }



  handleChangeFirstName(e) {
    this.setState({
      personalData: {
        firstName: e.target.value,
        lastName: this.state.personalData.lastName,
        birthDate: this.state.personalData.birthDate,
        email: this.state.personalData.email,
        phoneNumber: this.state.personalData.phoneNumber
      }
    })
  }

  handleChangeLastName(e) {
    this.setState({
      personalData: {
        firstName: this.state.personalData.firstName,
        lastName: e.target.value,
        birthDate: this.state.personalData.birthDate,
        email: this.state.personalData.email,
        phoneNumber: this.state.personalData.phoneNumber
      }
    })
  }

  handleChangeBirthDate(e) {
    this.setState({
      personalData: {
        firstName: this.state.personalData.firstName,
        lastName: this.state.personalData.lastName,
        birthDate: e.target.value,
        email: this.state.personalData.email,
        phoneNumber: this.state.personalData.phoneNumber
      }
    })
  }

  handleChangeEmail(e) {
    this.setState({
      personalData: {
        firstName: this.state.personalData.firstName,
        lastName: this.state.personalData.lastName,
        birthDate: this.state.personalData.birthDate,
        email: e.target.value,
        phoneNumber: this.state.personalData.phoneNumber
      }
    })
  }

  handleChangeNumber(e) {
    this.setState({
      personalData: {
        firstName: this.state.personalData.firstName,
        lastName: this.state.personalData.lastName,
        birthDate: this.state.personalData.birthDate,
        email: this.state.personalData.email,
        phoneNumber: e.target.value
      }
    })
  }
  render() {
    const { firstName, lastName, birthDate, email, phoneNumber } = this.state.personalData
    return (
      <div className="app">
        <Header />
        <div className="content">
          <InputsPersonalData addChild={In}
            firstName={this.handleChangeFirstName}
            lastName={this.handleChangeLastName}
            birthDate={this.handleChangeBirthDate}
            email={this.handleChangeEmail}
            number={this.handleChangeNumber}
          />

          <PreviewPersonalData
            firstName={firstName}
            lastName={lastName}
            birthDate={birthDate}
            email={email}
            phone={phoneNumber} />
        </div>
      </div>)
  }
}

class Education extends Component {
  render() {
    return (
      <div>
        <input placeholder="School name"></input>
        <input placeholder="Title of study"></input>
        <input placeholder="Date of study"></input>
      </div>

    )
  }
}

class AppComponent extends React.Component {
  state = {
    numChildren: 0
  }

  render () {
    const children = [];

    for (var i = 0; i < this.state.numChildren; i += 1) {
      children.push(<ChildComponent key={i} number={i} />);
    };

    return (
      <ParentComponent addChild={this.onAddChild}>
        {children}
      </ParentComponent>
    );
  }

  onAddChild = () => {
    this.setState({
      numChildren: this.state.numChildren + 1
    });
  }
}

const ParentComponent = props => (
  <div className="card calculator">
    <p><a href="#" onClick={props.addChild}>Add Another Child Component</a></p>
    <div id="children-pane">
      {props.children}
    </div>
  </div>
);



class InputsPersonalData extends Component {
  addEducation() {
    console.log("chuu")
    return <input></input>

  }

  render() {
    return (
      <div className="inputs">
        <input placeholder="First name" onChange={this.props.firstName}></input>
        <input placeholder="Last name" onChange={this.props.lastName}></input>
        <input placeholder="Date of birth" type='date' onChange={this.props.birthDate}></input>
        <input placeholder="Email" type='email' onChange={this.props.email}></input>
        <input placeholder="Phone number" type='number' onChange={this.props.number}></input>
        <button onClick={this.addEducation}>Add education</button>
      </div>)
  }
}

class Header extends Component {
  render() {
    return <div className="header"><h1>CV-Creator</h1></div>
  }
}

//A section to add your educational experience (school name, title of study, date of study)
//A section to add practical experience (company name, position title, main tasks of your jobs, date from and until when you worked for that company)

export default App;
